package com.braco96.cine.infrastructure.persistence.jpa; import jakarta.persistence.*; @Entity @Table(name="entradas", uniqueConstraints=@UniqueConstraint(columnNames={"sesion_id","fila","columna"})) public class JpaEntrada { @Id @GeneratedValue(strategy=GenerationType.IDENTITY) public Long id; @Column(name="sesion_id") public Long sesionId; public int fila; public int columna; @Column(name="cliente_id") public Long clienteId; public double precio; }
