package com.braco96.cine.infrastructure.persistence.jpa; import jakarta.persistence.*; import java.time.LocalDateTime; @Entity @Table(name="sesiones") public class JpaSesion { @Id @GeneratedValue(strategy=GenerationType.IDENTITY) public Long id; @Column(name="pelicula_id") public Long peliculaId; @Column(name="sala_id") public String salaId; @Column(name="fecha_hora") public LocalDateTime fechaHora; @Column(name="precio_base") public double precioBase; }
